const rawCsv = items[0].json.data;

// Parse CSV
const lines = rawCsv.trim().split('\n');
const headers = lines[0].replace(/^"|"$/g, '').split('","');
const jobs = lines.slice(1).map(line => {
  const cols = line.replace(/^"|"$/g, '').split('","');
  const obj = {};
  headers.forEach((h, i) => obj[h] = cols[i] || '');
  return obj;
});

// Friendly title date
const options = { year: 'numeric', month: 'long', day: 'numeric' };
const currentDate = new Date().toLocaleDateString('en-US', options);

// Build job rows
const htmlRows = jobs.map(job => `
  <tr>
    <td>${job.company}</td>
    <td>${job.title}</td>
    <td>${job.location}</td>
    <td>${job.date_posted}</td>
    <td><a href="${job.job_url}" target="_blank">View Job</a></td>

  </tr>
`).join('');

// Final HTML
const html = `
<html>
  <head>
<style>
  body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background-color: #f7f9fc;
    color: #333;
    margin: 0;
    padding: 0;
  }
  .container {
    max-width: 1200px;
    margin: auto;
    background: #ffffff;
    padding: 30px;
    border-radius: 10px;
    box-shadow: 0 2px 12px rgba(0,0,0,0.06);
  }
  h2 {
    text-align: center;
    color: #1a73e8;
    margin-top: 0;
    font-size: 24px;
  }
  table {
    width: 100%;
    border-collapse: separate;
    border-spacing: 0;
    margin-top: 25px;
    font-size: 14px;
  }
  th {
    background-color: #e6f2ff;
    color: #333;
    padding: 14px 12px;
    border: 1px solid #ccc;
    text-align: left;
  }
  td {
    padding: 12px;
    border: 1px solid #ddd;
    background-color: #fff;
    vertical-align: top;
  }
  tr:hover td {
    background-color: #f0f8ff;
  }
  tr:first-child th:first-child {
    border-top-left-radius: 8px;
  }
  tr:first-child th:last-child {
    border-top-right-radius: 8px;
  }
  tr:last-child td:first-child {
    border-bottom-left-radius: 8px;
  }
  tr:last-child td:last-child {
    border-bottom-right-radius: 8px;
  }
  a {
    color: #1a73e8;
    text-decoration: none;
  }
  a:hover {
    text-decoration: underline;
  }
  .footer {
    margin-top: 35px;
    text-align: center;
    font-size: 12px;
    color: #777;
  }
</style>
  </head>
  <body>
    <div class="container">
      <h2>üìå Top Job Opportunities ‚Äî Updated ${currentDate}</h2>
      <table>
        <thead>
          <tr>
            <th>Company</th>
            <th>Title</th>
            <th>Location</th>
            <th>Posted</th>
            <th>Job URL</th>

          </tr>
        </thead>
        <tbody>
          ${htmlRows}
        </tbody>
      </table>
      <div class="footer">
        This summary was generated automatically. Stay sharp and happy job hunting!
      </div>
    </div>
  </body>
</html>
`;


// (add to final return)
return [{
  json: {
    html: html,
    subjectDate: currentDate  // ‚Üê this is new
  }
}];
